import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios"; //returns a promise

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey =
    "pk_test_51H6S2OCq4gCL6ixNvpZqSUsHfeXjbzd6XfUf5cQvX2okoRXq1IgRkEBOQbWjsJyriih1HkbPM4V1yC4R4aJ5wdWE00kvJ21AT4";

  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amout: priceForStripe,
        token,
      },
    })
      .then((response) => {
        alert("Payment Successful! Laka da maar");
      })
      .catch((error) => {
        console.log("payment error: ", JSON.parse(error));
        alert(
          "There was an issue with your payment, Please make sure to use the righ credit card"
        );
      });
  };

  return (
    <StripeCheckout
      label="Pay Now"
      name="Voodoo Clothing Ltd."
      billingAddress
      shippingAddress
      image="https://svgshare.com/i/Cuz.svg"
      description={`Your total is $${price}`}
      amout={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeCheckoutButton;
